'use client'

import React from 'react'
import NextImage from 'next/image'
import { cn } from '@/utilities/ui'
import { getMediaUrl } from '@/utilities/getMediaUrl'
import { useMediaQuery } from '@/hooks/useMediaQuery'
import type { Media as MediaType } from '@/payload-types'

interface ArtDirectedMediaProps {
  landscapeImage: MediaType | string | number | null
  portraitImage?: MediaType | string | number | null
  alt?: string
  fill?: boolean
  priority?: boolean
  className?: string
  imgClassName?: string
  loading?: 'lazy' | 'eager'
  quality?: number
}

// A base64 encoded placeholder
const placeholderBlur =
  'data:image/png;base64,'

export const ArtDirectedMedia: React.FC<ArtDirectedMediaProps> = ({
  landscapeImage,
  portraitImage,
  alt: altProp,
  fill = false,
  priority = false,
  className,
  imgClassName,
  loading,
  quality = 90,
}) => {
  // Use media query to determine screen size (only applies if portrait image is available)
  const isMobileScreen = useMediaQuery('(max-width: 768px)')
  const shouldUsePortrait = isMobileScreen && portraitImage

  // Helper function to process media resource
  const processMediaResource = (resource: MediaType | string | number | null) => {
    if (!resource || typeof resource !== 'object' || !('url' in resource)) {
      return null
    }

    const { alt: resourceAlt, url, updatedAt, width, height } = resource
    const cacheTag = updatedAt
    const src = getMediaUrl(url, cacheTag)

    return {
      src,
      alt: resourceAlt || '',
      width: width || undefined,
      height: height || undefined,
    }
  }

  // Only process the image we actually need
  const targetImage = shouldUsePortrait ? portraitImage : landscapeImage
  const activeImageData = processMediaResource(targetImage)

  if (!activeImageData) {
    return null
  }

  const alt = altProp || activeImageData.alt || ''

  return (
    <div className={className}>
      <NextImage
        alt={alt}
        className={cn(imgClassName)}
        fill={fill}
        height={!fill ? activeImageData.height : undefined}
        placeholder="blur"
        blurDataURL={placeholderBlur}
        priority={priority}
        quality={quality}
        loading={loading}
        sizes="(max-width: 640px) 640px, (max-width: 768px) 768px, (max-width: 1024px) 1024px, (max-width: 1280px) 1280px, (max-width: 1536px) 1536px, (max-width: 1920px) 1920px, (max-width: 2560px) 2560px, (max-width: 3840px) 3840px, 5120px"
        src={activeImageData.src}
        width={!fill ? activeImageData.width : undefined}
      />
    </div>
  )
}
